import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import '../../node_modules/bootstrap/dist/css/bootstrap.css'


function Details(){
    var {id} = useParams();

    const [movieDetails, setMovieDetails] = useState({
            ID            :   0,
            Title         :   "",
            Discription   :   "",
            Cast          :   [],
            Genre         :   "",
            url           :   "",
            img           :   ""    
    });
    useEffect(()=>{
        var helper = new XMLHttpRequest();
        helper.onreadystatechange = () =>{
            if(helper.readyState == 4 && 
                helper.status == 200){
                    var dataReceived = JSON.parse(helper.responseText);
                    setMovieDetails(dataReceived);
                }
        };
        helper.open("GET", `http://localhost:3000/Data/${id}.json`);
        helper.setRequestHeader("Content-Type", "application/json");
        helper.send();
    }, []);
    return (<div className="card">
                <img className="card-img-top" src={movieDetails.img} alt="Card image cap" width={1920} height={720}/>
                <div className="card-body">
                    <p className="card-text">{movieDetails.Discription}</p>
                </div>
            </div>)
}


export default Details;
